function setup_datagrid(ajaxurl,sortcolumns,filters,clickablerows,order,per_page,drawcallback,action_column_position){var columnHeaders=[];$('#ajaxtable th').each(function(){columnHeaders.push($(this).attr('class'));});var ajaxtable=$('#ajaxtable').dataTable({"processing":true,"serverSide":false,"stateSave":true,"stateDuration":120,"pageLength":per_page,"lengthMenu":[[10,20,50,-1],[10,20,50,"All"]],"pagingType":"numbers","order":[[1,'asc']],"stripClasses":['odd','even'],"dom":"<'row'lpi>"+"<'row'<'col-sm-12'tr>>","jQueryUI":false,"ajax":{url:ajaxurl,type:'POST'},"rowCallback":function(row,data){if(action_column_position=='Left'){$(row).attr('id','row_'+data[1]);}else if(action_column_position=='Right'){$(row).attr('id','row_'+data[0]);}
if(clickablerows){$(row).bind('mouseover',function(){$(this).addClass('rowhover');});$(row).bind('mouseout',function(){$(this).removeClass('rowhover');});if(action_column_position=='Left'){$(row).children('td:first').each(function(){$(this).addClass('actions');});}else if(action_column_position=='Right'){$(row).children('td:last').each(function(){$(this).addClass('actions');});}
$(row).children('td').each(function(){if(!$(this).hasClass('actions')&&$(this).find('.multiselect').size()==0){$(this).bind('click',function(){window.location=$('#'+$(row).attr('id')+" a[class~='edit']").attr('href');});}});}
$(row).find('a[class="delete"]').bind('click',function(event){event.preventDefault();$.getJSON(this.href,{},function(result){print_message(result.message,result.type);if(result.type=='success'){$(row).hide(200);}});});return row;},"drawCallback":drawcallback,"order":order,"initComplete":function(settings,json){if(undefined!=$('#export_to_pdf')){}
$.each($('.multiselect'),function(key,item){var callback_url=$(item).attr('data-callback');$(item).multiselect({nonSelectedText:$(item).attr('data-nonSelectedText'),onDropdownHide:function(event){$.post(base_url+callback_url,{values:$(item).val()},function(data){print_message(data.message,data.type);},'json');}});});}});var table=ajaxtable.api();yacdf_columns=[];$.each(sortcolumns,function(key,item){var filter_type='text';var filter_label=undefined;var column_data_type='text';item_name=$(table.column(key).header()).attr('class');if(item_name.match(/_date/)){filter_type='range_date';}else{filter_label='Search';}
if(item_name.match(/statuses/)){filter_type='text';column_data_type='html';}
if(item_name.match(/actions/)){return;}
if(item_name.match(/statuses/)){yacdf_columns.push({column_number:key,filter_type:'custom_func',filter_default_label:filter_label,filter_reset_button_text:false,column_data_type:column_data_type,custom_func:status_filter,custom_func_options:{test:'yes'}});}else{yacdf_columns.push({column_number:key,filter_type:filter_type,filter_default_label:filter_label,filter_reset_button_text:false,column_data_type:column_data_type,html_data_type:'text'});}});yadcf.init(table,yacdf_columns);return ajaxtable;}
function status_filter(filterVal,columnVal,col_num,contents){var text_value='';$(contents).find('option[selected="selected"]').each(function(key,item){text_value+=$(item).html();});if(filterVal==''){return true;}
var re=new RegExp(filterVal,'ig');return text_value.match(re);}